INSERT INTO `creature_ai_texts` VALUES (-460, 'Wait...you are not my husband.  But he must have sent you.  And you...look..delicious!', NULL, NULL, NULL, '等等……你不是我的丈夫。但肯定是他派你来的。你看起来……很可口！', NULL, NULL, NULL, NULL, 0, 0, 7, 0, 111, '314');
