INSERT INTO `creature_ai_texts` VALUES (-10337, 'Kill them before they get away!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16250, '5355/16769/1410');
INSERT INTO `creature_ai_texts` VALUES (-10338, 'This belongs to Firewing Point.  You\'ll not be putting your grubby hands on it!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15528, '18548');
INSERT INTO `creature_ai_texts` VALUES (-10339, 'My weapon be thirsty!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 1908, 'Common Troll Text');
INSERT INTO `creature_ai_texts` VALUES (-10340, '%s\'s eyes glow red as he begins to cackle madly!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 7, 0, 16771, '18827');
INSERT INTO `creature_ai_texts` VALUES (-10341, 'So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12617, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10342, 'Sure enough, scout came runnin\' back, talkin\' bout some orcs down the path. They hadn\'t seen us yet so we figured we\'d go get acquainted.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12618, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10343, 'Big red bastards! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12625, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10344, '%s nods.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 12627, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10345, 'Fel orcs...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12628, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10346, 'I dunno, grunt. I just - don\'t - know...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12636, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10347, 'But that\'s not the worst of it. Before I could pull us back, a group of \'em spotted us and attacked.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12637, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10348, 'Lost some of my best soldiers that day...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12638, '16589');
INSERT INTO `creature_ai_texts` VALUES (-10349, 'Yer slop is ready. Come n\' get it...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12531, '16585');
INSERT INTO `creature_ai_texts` VALUES (-10350, 'And if I hear that you palookas complained to Nazgrel again, it\'ll be one of ya\'s that I serve up next time...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12532, '16585');
INSERT INTO `creature_ai_texts` VALUES (-10351, 'If my stomach wasn\'t threatening to dislodge itself from my body and leave me for the refuse that we give to the prisoners, I might be insulted by this... this food.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12550, '16590');
INSERT INTO `creature_ai_texts` VALUES (-10352, 'Do I detect a hint of cinnamon? Or is that nutmeg? Whatever it is, one thing\'s for sure - nobody cooks a donkey like you, Cookie.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12547, '16590');
INSERT INTO `creature_ai_texts` VALUES (-10353, 'I\'ve seen gnomes work harder than you, peon! Little girl gnomes - with pig tails! MOVE IT, MOVE IT, MOVE IT!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12551, '16584');
INSERT INTO `creature_ai_texts` VALUES (-10354, 'What kinda orcs, chief?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12619, '16590');
INSERT INTO `creature_ai_texts` VALUES (-10355, 'You mean...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12491, '16590');
INSERT INTO `creature_ai_texts` VALUES (-10356, 'But how, chief? I thought the curse was lifted...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12635, '16590');
INSERT INTO `creature_ai_texts` VALUES (-10357, 'The warchief\'s put a price on Arazzius\'s head! As of right now, that no-good piece of demon refuse is enemy number one.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16391, '19255');
INSERT INTO `creature_ai_texts` VALUES (-10358, 'Yea, we got problems. I think that\'s crystal clear. The question is, what are we gonna do about it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16392, '19255');
INSERT INTO `creature_ai_texts` VALUES (-10359, 'I\'m not gonna lie to you, soldiers. We\'re getting our butts kicked out there! Now which one of you is orc enough to do something about it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 16390, '19255');
INSERT INTO `creature_ai_texts` VALUES (-10360, 'What is it that you want from me, shaman?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19608, '21769');
INSERT INTO `creature_ai_texts` VALUES (-10361, 'Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19609, '21769');
INSERT INTO `creature_ai_texts` VALUES (-10362, 'Pray to your gods, shaman. Perhaps they will come to your aid. The Horde, however, cannot...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19610, '21769');
INSERT INTO `creature_ai_texts` VALUES (-10363, 'Wow! Look at his muscles!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 1, 16226, 'Maghar Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10364, 'Will he win? Can he win?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 1, 16228, 'Maghar Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10365, 'He\'s so well disciplined!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 1, 16230, 'Maghar Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10366, 'I wanna be just like the pitfighter!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 1, 16227, 'Maghar Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10367, 'How does he do it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 1, 16231, 'Maghar Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10368, 'The pitfighter will be victorious! Just look at him!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 1, 16229, 'Maghar Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10369, 'I don\'t wanna play anymore!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15122, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10370, 'Duck!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15117, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10371, 'I miss my mommy.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15121, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10372, 'OGRE! You\'re the ogre! HA-HA!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15118, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10373, 'Can we stop running now?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15120, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10374, 'Don\'t go close to the lake! It\'s haunted!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15119, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10375, 'Tag! You\'re it!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15114, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10376, 'Get out of the way!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 1, 0, 15116, '18299/18296');
INSERT INTO `creature_ai_texts` VALUES (-10377, 'You dare fight me?  I was trained by Illidan himself!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19578, '21171');
INSERT INTO `creature_ai_texts` VALUES (-10378, 'If I see any lazy peons here, I\'ll load them into a catapult and launch them off to the Black Temple!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 16557, '19362');
INSERT INTO `creature_ai_texts` VALUES (-10379, 'Any time any of you peons want to take a break, the graveyard is right over there.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16559, '19362');
INSERT INTO `creature_ai_texts` VALUES (-10380, 'Last time someone went out to try to collect the bounty on Netherock, all we got back was a compressed layer of foolhardy adventurer!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19660, '19541');
INSERT INTO `creature_ai_texts` VALUES (-10381, 'Maybe I should gather up some other agents and head out there after this shift?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17115, '19541');
INSERT INTO `creature_ai_texts` VALUES (-10382, 'I can\'t believe that Papa Wheeler thinks he\'s going to dupe anyone into going out to Manaforge B\'naar!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 17114, '19541');
INSERT INTO `creature_ai_texts` VALUES (-10383, 'We can\'t let a failing servo slow down the construction of the X-52 Nether-Rocket!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 17117, '19541');
INSERT INTO `creature_ai_texts` VALUES (-10384, 'Netherock?!  That thing\'ll squish you flat with one step of its massive foot!  No thanks!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 19659, '19541');
INSERT INTO `creature_ai_texts` VALUES (-10385, 'Bron! \'Ey, Bron! Hit me!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16586, '19394');
INSERT INTO `creature_ai_texts` VALUES (-10386, 'Nae, that\'s fer wimps. Hit me with yer hammer!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16589, '19394');
INSERT INTO `creature_ai_texts` VALUES (-10387, 'DO IT, YE PANSY!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16591, '19394');
INSERT INTO `creature_ai_texts` VALUES (-10388, '...THAT WAS GREAT!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16593, '19394');
INSERT INTO `creature_ai_texts` VALUES (-10389, 'I don\'t get it! Are ya blind? Can ya not see the demons bangin\' on our door? Who am I gonna send out to talk to your shaman friends? Point \'em out!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19612, '21773');
INSERT INTO `creature_ai_texts` VALUES (-10390, 'Let my darkness engulf you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19907, '18683');
INSERT INTO `creature_ai_texts` VALUES (-10391, 'In the void, no one can hear you scream!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19908, '18683');
INSERT INTO `creature_ai_texts` VALUES (-10392, 'I shall send your soul into the infinite void!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19906, '18683');
INSERT INTO `creature_ai_texts` VALUES (-10393, 'The creatures of this place are strong, wayfarer. You\'ll need ample ammunition if you hope to survive. You\'re in luck - I am well-stocked with fine arrows, bullets, and throwing weapons. The price is reasonable, considering circumstances.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17002, '19534');
INSERT INTO `creature_ai_texts` VALUES (-10394, 'Ahh, another powerful traveler. The nether can be dangerous; you\'ll want the most potent spells at your disposal. I carry the finest reagents, common and exotic. Come, buy - you won\'t be sorry!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17001, '19535');
INSERT INTO `creature_ai_texts` VALUES (-10395, '%s whispers softly in reverent tones under her breath.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 18715, '21066');
INSERT INTO `creature_ai_texts` VALUES (-10396, '%s examines the information on the wanted poster.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 18719, '18098');
INSERT INTO `creature_ai_texts` VALUES (-10397, '%s begins to metamorphose...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 18589, '20747');
INSERT INTO `creature_ai_texts` VALUES (-10398, '%s splits open!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 1410, '20747');
INSERT INTO `creature_ai_texts` VALUES (-10399, 'How dare you disturb the master\'s dinner!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 13438, '16414');
INSERT INTO `creature_ai_texts` VALUES (-10400, 'That was wasted on the likes of you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 13437, '16414');
INSERT INTO `creature_ai_texts` VALUES (-10401, 'That was a 200 year old Pupellyverbos Port!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 13436, '16414');
INSERT INTO `creature_ai_texts` VALUES (-10402, 'How dare you spill the master\'s wine!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 13435, '16414 on Aggro');
INSERT INTO `creature_ai_texts` VALUES (-10403, 'Impudent outsiders!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 13876, '16414 on Aggro');
INSERT INTO `creature_ai_texts` VALUES (-10404, 'Meddling fools! You will pay with your lives!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 13877, '16414 on Aggro');
INSERT INTO `creature_ai_texts` VALUES (-10405, 'You\'ll never make it out alive....', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 13879, '16414 on Death');
INSERT INTO `creature_ai_texts` VALUES (-10406, '%s rushes forward, carefully watching its enemy.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 19467, '19982');
INSERT INTO `creature_ai_texts` VALUES (-10407, '%s laughs at your clumsiness.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 153, 18658, '20416');
INSERT INTO `creature_ai_texts` VALUES (-10408, 'ATTENTION!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 20704, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10409, 'Alright, quiet down. We have a lot to do and very little time to do it in, people.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24210, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10410, 'I\'ve stood with some of you draenei before at Skettis, and others in Shadowmoon Valley. But now we\'re on Azeroth, and the situation looks grim.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24211, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10411, 'First though, you sin\'dorei listen up. I run a tight ship... this isn\'t going to be like what you\'re used to with the Scryers. Pull your weight, or get back on the boat!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24212, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10412, 'Now that that\'s out of the way, welcome to the Isle of Quel\'Danas. The Sunwell\'s on the plateau to the south, and Prince Kael\'thas is holed up in the Magisters\' Terrace to the east.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24213, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10413, 'We\'re here as support for the so-called \'adventurers\' that the brass has called in. We\'ll help them hold the key points on the island, and keep the heat off so that they can do their jobs.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24214, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10414, 'If none of you ladies have any questions, you can get further instructions and assignments from your squad leaders. That\'s it. Good luck!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24215, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10415, 'Welcome to the Sunwell. Despite the mild temperatures and scenic vistas, you\'re not on vacation, ladies and gentlemen!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24216, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10416, 'The current situation looks like this: we\'re holding what we can while those with more experience and better equipment are moving against Prince Kael\'thas.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24217, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10417, 'I know, we thought he was dead too, but that\'s not the case. He\'s trying to use the Sunwell to bring the master of the Burning Legion, Kil\'jaeden, into this world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24218, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10418, 'Let me assure you people that if he\'s successful, this world will suffer far worse than Outland ever did! So, let\'s make sure that doesn\'t come to pass.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24219, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10419, 'Warriors will be deployed to hold key positions, while you marksmen will be assigned to keep the enemy fliers at bay.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24220, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10420, 'I\'m awaiting orders on your specific assignments. In the meantime, stay here and be ready to move at a moment\'s notice!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24221, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10421, 'Hello to you new recruits. I\'m Drill Sergeant Bahduum. For the next few days, you\'re going to receive orientation.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24195, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10422, 'The Isle of Quel\'Danas has been overrun by the forces of Prince Kael\'thas Sunstrider. Intel had him pegged as deceased. Intel was wrong.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24196, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10423, 'Up on the plateau, he and his demon friends are trying to reignite the Sunwell and open a rift to bring their head-honcho, Kil\'Jaeden, back into the world.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24197, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10424, 'Our job is to clear out as much of his fodder as we can, while supporting the specialists that the powers-that-be are having brought in.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24198, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10425, 'Needless to say, a lot of us won\'t be going home. But if you remember your training and keep your wits about you, you\'ll greatly increase your chances.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24199, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10426, 'That\'s all I have to say for now. Stay focused, boys and girls.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24200, '25162');
INSERT INTO `creature_ai_texts` VALUES (-10427, 'Shattered Sun scum!  Fire at will!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 24175, '25132');
INSERT INTO `creature_ai_texts` VALUES (-10428, 'Keep your eye on the demons.  We\'re not concerned with killing Scourge today.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 24184, '25144');
INSERT INTO `creature_ai_texts` VALUES (-10429, 'Dragonhawk incoming from the west!  Shoot that $c down!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 24173, '25144');
INSERT INTO `creature_ai_texts` VALUES (-10430, 'Wow! Look at his muscles!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, 16233, 'Kurenai Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10431, 'Will he win? Can he win?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, 16235, 'Kurenai Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10432, 'He\'s so well disciplined!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, 16237, 'Kurenai Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10433, 'I wanna be just like the pitfighter!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, 16234, 'Kurenai Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10434, 'How does he do it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, 16238, 'Kurenai Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10435, 'The pitfighter will be victorious! Just look at him!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 1, 16236, 'Kurenai Pitfighter Spectator Text');
INSERT INTO `creature_ai_texts` VALUES (-10436, 'Chubis Chubisimo!  Get those things fixed... we haven\'t got all day!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 19378, '19645');
INSERT INTO `creature_ai_texts` VALUES (-10437, 'How much am I paying you?  You call that working?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 19381, '19645');
INSERT INTO `creature_ai_texts` VALUES (-10438, 'When I was your age I could have had all of those shredders fixed before breakfast and still had time for all of my other chores.  In the Winterspring snow, no less.  And it\'s uphill both ways there!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 19379, '19645');
INSERT INTO `creature_ai_texts` VALUES (-10439, 'Chubis, after you\'re done there, make sure that you put another coat of wax on the Wheeler mobile.  I want to take mama out for a spin tonight.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 19380, '19645');
INSERT INTO `creature_ai_texts` VALUES (-10440, 'I just knew that I shouldn\'t have fired Beebo!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 19382, '19645');
INSERT INTO `creature_ai_texts` VALUES (-10441, 'Okay, if you can manage to get even one of those fixed, I\'ll let you take a ten minute break.  How\'s that sound?  Pretty good, huh?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 5, 19385, '19645');
INSERT INTO `creature_ai_texts` VALUES (-10442, 'We will not fail!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 23332, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10443, 'Death to the Last Guardian!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15171, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10444, 'We are not finished!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15174, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10445, 'More will take my place.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15167, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10446, 'A minor victory...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15175, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10447, 'Your efforts... are in vain.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15168, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10448, 'We will not be stopped!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15166, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10449, 'The wizard will fall!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15164, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10450, 'The rift must be protected!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15182, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10451, 'You are running out of time!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15183, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10452, 'Victory or death!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15184, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10453, 'Quake, mortals!  Quake and kneel before your destroyer!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 16437, '19288');
INSERT INTO `creature_ai_texts` VALUES (-10454, '%s lets out a shrill shriek, calling for help from a nearby kaliri.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 21260, '21650');
INSERT INTO `creature_ai_texts` VALUES (-10455, 'You!  You don\'t belong here!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 15786, '18718');
INSERT INTO `creature_ai_texts` VALUES (-10459, 'It\'s the end of the world!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18484, '17931');
INSERT INTO `creature_ai_texts` VALUES (-10460, 'We\'re doomed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18483, '17931');
INSERT INTO `creature_ai_texts` VALUES (-10461, 'Puny $r... you were a... horrible $g king : queen;.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21050, 'Generic Blades Edge Ogre Death Text (If Player has Completed Quest 11000)');
INSERT INTO `creature_ai_texts` VALUES (-10462, 'Agh... me am...dead.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21032, 'Generic Blades Edge Ogre Death Text (If Player has Completed Quest 11000)');
INSERT INTO `creature_ai_texts` VALUES (-10463, 'Me go to... Ogri\'la.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21033, 'Generic Blades Edge Ogre Death Text (If Player has Completed Quest 11000)');
INSERT INTO `creature_ai_texts` VALUES (-10464, 'Me honored... $g king : queen; kill me.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21034, 'Generic Blades Edge Ogre Death Text (If Player has Completed Quest 11000)');
INSERT INTO `creature_ai_texts` VALUES (-10465, '$G King : Queen; $n, me die now.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21035, 'Generic Blades Edge Ogre Death Text (If Player has Completed Quest 11000)');
INSERT INTO `creature_ai_texts` VALUES (-10466, '$G King : Queen; think... there really is... an Ogri\'la?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21049, 'Generic Blades Edge Ogre Death Text (If Player has Completed Quest 11000)');
INSERT INTO `creature_ai_texts` VALUES (-10467, 'How do you defeat that which has no life?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18382, '20452');
INSERT INTO `creature_ai_texts` VALUES (-10468, 'Pathetic. Is that the best you can do?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18347, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10469, 'Now you will know fear!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18342, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10470, 'You\'re weak! COME ON!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18341, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10471, 'Your battle prowess is nothing compared to mine!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18345, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10472, 'Taste pure energy!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18344, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10473, 'Your death will serve as a message to all that would dare set foot upon Ethereum territory!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18346, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10474, 'Run away while you still can!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18348, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10475, 'For Salhadaar! For the Nexus-King!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18343, '20453');
INSERT INTO `creature_ai_texts` VALUES (-10476, 'Stupid, squishy $r. That weapon mine now! Give!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19510, '20730');
INSERT INTO `creature_ai_texts` VALUES (-10477, '%s brushes up against $n and farts like only an ogre can.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 20926, 'Generic Blades Edge Ogre /fart emote response');
INSERT INTO `creature_ai_texts` VALUES (-10478, 'Me like to fart, too!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 20927, 'Generic Blades Edge Ogre /fart emote response');
INSERT INTO `creature_ai_texts` VALUES (-10479, 'The %s cowers at the sight of his angered $g king : queen;.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 20925, 'Generic Blades Edge Ogre /angry emote response');
INSERT INTO `creature_ai_texts` VALUES (-10480, 'Aw, me not think it work out between us.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 20928, 'Generic Blades Edge Ogre /kiss emote response');
INSERT INTO `creature_ai_texts` VALUES (-10481, 'What me do wrong, my $g king : queen;?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 16, 20929, 'Generic Blades Edge Ogre /point emote response');
INSERT INTO `creature_ai_texts` VALUES (-10482, 'You won\'t get away this time!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 12117, '16238');
INSERT INTO `creature_ai_texts` VALUES (-10483, 'The sands of time shall be scattered to the winds!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 15179, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10484, 'History is about to be rewritten!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 15180, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10485, 'Let the siege begin!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 15181, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10486, 'You will never defeat us all!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 15185, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10487, 'You will accomplish nothing!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 15186, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10488, 'No! The rift...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 15187, 'Black Morass');
INSERT INTO `creature_ai_texts` VALUES (-10489, 'For Silvermoon! For Sunstrider!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 14183, '17591');
INSERT INTO `creature_ai_texts` VALUES (-10490, 'Your death will make a fitting tribute to the King!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 14182, '17591');
INSERT INTO `creature_ai_texts` VALUES (-10491, 'Your money or your life! Actually, just your life will do...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 14181, '17591');
INSERT INTO `creature_ai_texts` VALUES (-10492, '%s begins to release his power in desperation!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 18929, '20458');
INSERT INTO `creature_ai_texts` VALUES (-10493, 'Mog\'dorg is a fool to think that one of the smaller races will be our savior.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21025, '18693 OOC');
INSERT INTO `creature_ai_texts` VALUES (-10494, 'Do not listen to Mog\'dorg\'s lies!  Ogri\'la is a myth!  We make our own heaven here with the blood of any that would divide and subjugate us!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21029, '18693 OOC');
INSERT INTO `creature_ai_texts` VALUES (-10495, 'The time has come.  Strike down every creature that stands in the way of the clans!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21028, '18693 OOC');
INSERT INTO `creature_ai_texts` VALUES (-10496, 'Will we never learn to work together?  Or will we simply kill each other off and be forgotten in time.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19882, '18693 OOC');
INSERT INTO `creature_ai_texts` VALUES (-10497, 'Do not bow to Mog\'dorg\'s puppet!  He would replace our gronn overlords with a liege of lesser stock.  Why should we replace one master with another?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21027, '18693 OOC');
INSERT INTO `creature_ai_texts` VALUES (-10498, 'If only the leaders of the clans would listen.  Unity is the only answer.  All other paths lead to our destruction.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19883, '18693 OOC');
INSERT INTO `creature_ai_texts` VALUES (-10499, 'We should be uniting the Ogre clans, not fighting each other.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19881, '18693 OOC');
INSERT INTO `creature_ai_texts` VALUES (-10500, 'The Ogre clans will not be subjugated.  Not by the gronn, and not by a puny $r like you!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 21024, '18693 Aggro');
INSERT INTO `creature_ai_texts` VALUES (-10501, 'My message of unity is meant for the Ogre clans, not for you, $r!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19884, '18693 Aggro');
INSERT INTO `creature_ai_texts` VALUES (-10502, 'I decree that you are an enemy of the Ogre clans.  Punishment is death!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19886, '18693 Aggro');
INSERT INTO `creature_ai_texts` VALUES (-10503, 'I will not allow you to kill anymore of my kind!  Prepare to die!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19885, '18693 Aggro');
INSERT INTO `creature_ai_texts` VALUES (-10510, 'Mine! You shall not take this place!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 33, 0, 24005, '24960');
INSERT INTO `creature_ai_texts` VALUES (-10511, 'Get away, this place is ours!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 33, 0, 24004, '24966');
INSERT INTO `creature_ai_texts` VALUES (-10512, 'The rift\'s power is ours!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 33, 0, 24003, '24966');
INSERT INTO `creature_ai_texts` VALUES (-10513, 'Hal...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17966, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10514, 'Yea, Nat?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17967, '20342');
INSERT INTO `creature_ai_texts` VALUES (-10515, 'Had that dream again last night...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17968, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10516, 'Which one?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17969, '20342');
INSERT INTO `creature_ai_texts` VALUES (-10517, 'That one dream... The crazy one. Remember?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17970, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10518, 'Tarren Mill is destroyed by some crazy force of undead and forever more becomes an enemy to Southshore.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17971, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10519, 'Could ya imagine such a thing, Hal? Could ya?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17972, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10520, 'Impossible!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18118, '20342');
INSERT INTO `creature_ai_texts` VALUES (-10521, 'That one where thousands of people are all watching us from up in the sky...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18060, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10522, 'We\'re like their puppets - we dance and cry and fight and say silly things for their amusement.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18061, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10523, 'Sometimes, even when I\'m awake, I think they\'re watching us. Maybe they\'re watching us right now...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18062, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10524, 'Nat, I\'ve heard a whopper or two in my day but that one takes the cake.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17992, '20342');
INSERT INTO `creature_ai_texts` VALUES (-10525, 'So I\'m on some mountain with some big ol\' tree. Bunch of elves runnin\' around all over...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17976, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10526, 'Out of nowhere, some 300 foot tall demon or somethin\' walks up and starts climbin\' the tree...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17977, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10527, 'And if that weren\'t crazy enough, that big demon gets killed by a bunch of little floating light things... Oh, I was also a female elf in that one... Yep...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17978, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10528, 'In this dream, I was fishin\' master of the world. I moved to some place called Kalimdor...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17979, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10529, 'And people from all over the world come to me to pay homage... and learn to fish.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17980, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10530, 'I\'m like some kind of fishing god...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17981, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10531, 'This one\'s real grim... So the king\'s kid, Arthas... Well he goes out to battle evil, along with Uther... *Nat pats his brow dry*', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17985, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10532, 'But tragically, Arthas is consumed by the evil and becomes evil himself...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17986, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10533, 'Well, he comes back to the king all pretendin\' to be nice, draws his sword, and runs the king through, elbow to ... well you know... kills him on the spot.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17987, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10534, 'I can\'t even believe this one... You know those two loud-mouthed ruffians, Foror and Tigule?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17982, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10535, 'Well in this dream, they somehow end up inventing something called ice-cream and flavoring it with strawberries... Well, long story short, they end up striking it rich!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17983, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10536, 'If that weren\'t crazy enough, they decide to quit the ice-cream business and become adventurers... They travel all over the place and finally disappear into some portal. I woke up in a cold sweat after that one...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17984, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10537, 'That one where i\'m in Durnholde and that one orc that Blackmoore keeps as his personal slave breaks out...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17973, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10538, 'He ends up destroying the whole keep, freeing all the orcs we\'re keeping in the camps, and rising to power as the king of orcs... Warchief or somethin\'.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17974, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10539, 'Crazy, isn\'t it?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17975, '20344');
INSERT INTO `creature_ai_texts` VALUES (-10540, 'No way!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 22397, '20342');
INSERT INTO `creature_ai_texts` VALUES (-10541, 'It\'ll never happen, Nat.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17988, '20342');
INSERT INTO `creature_ai_texts` VALUES (-10542, 'What you got in that pipe, Nat?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 17993, '20342');
INSERT INTO `creature_ai_texts` VALUES (-10543, 'Keep your voice down, Helcular. Strangers abound...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18033, '20350');
INSERT INTO `creature_ai_texts` VALUES (-10544, 'Necromancy. It is called necromancy. And yes, I have it within my power to bless you with this gift.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18042, '20350');
INSERT INTO `creature_ai_texts` VALUES (-10545, 'That is none of your concern, Helcular, as you are neither Kirin Tor nor a necromancer.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18044, '20350');
INSERT INTO `creature_ai_texts` VALUES (-10546, 'But to be perfectly frank, I do not give a damn what the Kirin Tor think! They are fools, set in their archaic ways.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18050, '20350');
INSERT INTO `creature_ai_texts` VALUES (-10547, 'In due time, Helcular... All in due time...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18052, '20350');
INSERT INTO `creature_ai_texts` VALUES (-10548, 'So you can teach me this... this...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18041, '20353');
INSERT INTO `creature_ai_texts` VALUES (-10549, 'And the Kirin Tor? What have they to say of necromancy?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18043, '20353');
INSERT INTO `creature_ai_texts` VALUES (-10550, 'Then teach me, Kel\'Thuzad. Teach me everything you know...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 18051, '20353');
INSERT INTO `creature_ai_texts` VALUES (-10551, 'Well done, soldiers! We weren\'t about to let the filthy animals of the Horde show us up!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 22, 16578, '19316');
INSERT INTO `creature_ai_texts` VALUES (-10552, 'We\'re gonna melt this hunk of junk down and make weapons with the ingots! Let\'s see how the Legion likes a taste of fel reaver served up Alliance style!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 22, 16577, '19316');
INSERT INTO `creature_ai_texts` VALUES (-10553, 'Tear it down, soldiers! We\'re shippin\' this thing back to Stormwind!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 22, 16576, '19316');
INSERT INTO `creature_ai_texts` VALUES (-10554, 'Let them throw another one of these overgrown tin cans at us! We\'ll show them what the Alliance is made of!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 7, 22, 16579, '19316');
INSERT INTO `creature_ai_texts` VALUES (-10555, 'Life energy to... consume.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11250, 1, 0, 0, 20045, '20870');
INSERT INTO `creature_ai_texts` VALUES (-10556, 'The shadow... will engulf you.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11253, 1, 0, 0, 20048, '20870');
INSERT INTO `creature_ai_texts` VALUES (-10557, 'Darkness... consumes all.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11254, 1, 0, 0, 20049, '20870');
INSERT INTO `creature_ai_texts` VALUES (-10558, 'This vessel is empty.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11251, 1, 0, 0, 20046, '20870');
INSERT INTO `creature_ai_texts` VALUES (-10559, 'No... more... life.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11252, 1, 0, 0, 20047, '20870');
INSERT INTO `creature_ai_texts` VALUES (-10560, 'The Void... beckons.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 11255, 1, 0, 0, 20050, '20870');
INSERT INTO `creature_ai_texts` VALUES (-10561, 'Ah, damn it all! Clever trap. Too bad you\'ll never live to tell the tale...', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 19184, '21409');
INSERT INTO `creature_ai_texts` VALUES (-10562, 'Foolish druid! Dividing your essence has weakened you, opening the door for my master to dominate the Emerald Dream. Your spirit and your body will die here!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 1, 0, 0, 20565, '22915');
INSERT INTO `creature_ai_texts` VALUES (-10563, 'Do you think that demons will play fair? These beasts are more savage than orcs and more cunning than the undead. Do not attempt to engage Legion by normal standards of war.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 16454, '19308');
INSERT INTO `creature_ai_texts` VALUES (-10564, 'How many more soldiers need to die before we start paying attention out there?', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 16453, '19308');
INSERT INTO `creature_ai_texts` VALUES (-10565, 'This war - and don\'t mistake this for anything but a war - is not going to win itself. If we do not fight, scratch, claw, and kick for every speck of land on this shattered world, all will be lost.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 16456, '19308');
INSERT INTO `creature_ai_texts` VALUES (-10566, 'I regret to inform you all that we sent home another ten soldiers today - in body bags.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 1, 16455, '19308');
INSERT INTO `creature_ai_texts` VALUES (-10567, '%s taps his foot impatiently.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 12590, '16213');
INSERT INTO `creature_ai_texts` VALUES (-10568, '%s glares at the guards.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 12591, '16213');
INSERT INTO `creature_ai_texts` VALUES (-10569, '%s sighs in apparent frustration.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 12592, '16213');
INSERT INTO `creature_ai_texts` VALUES (-10570, '%s peers inside the tower and then clenches his jaw.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 2, 0, 0, 12593, '16213');
INSERT INTO `creature_ai_texts` VALUES (-10571, 'Let\'s pick up the pace!  These ballistas aren\'t going to fix themselves!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16760, '19446');
INSERT INTO `creature_ai_texts` VALUES (-10572, 'Keep up the pace boys!  We need these supplies moved and weapons fixed!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16759, '19446');
INSERT INTO `creature_ai_texts` VALUES (-10573, 'Come on!  Our boys are out there and they need these supplies ready!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, 16758, '19446');
